import React, { Fragment, useEffect } from "react";

import mealsImage from "../../assets/meals.jpg";
import classes from "./Header.module.css";
import HeaderCartButton from "./HeaderCartButton";

import { get, post } from "../../services/httpRequest";

const Header = (props) => {
  // useEffect(() => {
  //   get().then((res) => console.log(res));
  //   post();
  // }, []);

  async function fetchData() {
    try {
      const data = await get(); // Lấy toàn bộ dữ liệu từ file JSON
      const users = data.users;
      const posts = data.posts;
      console.log(users); // Xử lý dữ liệu người dùng trả về
      console.log(posts); // Xử lý dữ liệu bài viết trả về

      const newUser = await post("users", { name: "demo", age: 22 });
      console.log(newUser); // Xử lý dữ liệu người dùng mới trả về từ yêu cầu POST
    } catch (error) {
      console.error(error); // Xử lý lỗi nếu có
    }
  }

  fetchData();

  // useEffect(() => {
  //   const fetchData = async () => {
  //     try {
  //       const response = await fetch("/db.json");
  //       const jsonData = await response.json();
  //       console.log(jsonData);
  //     } catch (error) {
  //       console.error("Error fetching data:", error);
  //     }
  //   };

  //   fetchData();
  // }, []);

  return (
    <Fragment>
      <header className={classes.header}>
        <h1>ReactMeals</h1>
        <HeaderCartButton onShowCart={props.onShowCart} />
      </header>
      <div className={classes["main-image"]}>
        <img src={mealsImage} alt="a table full of delicious food!"></img>
      </div>
    </Fragment>
  );
};

export default Header;
