import React, { Fragment, useEffect } from "react";

import mealsImage from "../../assets/meals.jpg";
import classes from "./Header.module.css";
import HeaderCartButton from "./HeaderCartButton";

import { get, post } from "../../services/httpRequest";

const Header = (props) => {
  useEffect(() => {
    get().then((res) => console.log(res));
  }, []);

  const fs = require("fs");

  // Đường dẫn đến tệp tin JSON
  const filePath = "./data.json";

  // Đọc dữ liệu từ tệp tin JSON
  const getData = () => {
    try {
      const data = fs.readFileSync(filePath, "utf-8");
      return JSON.parse(data);
    } catch (error) {
      console.error("Lỗi khi đọc dữ liệu:", error);
      return null;
    }
  };

  // Ghi dữ liệu vào tệp tin JSON
  const saveData = (data) => {
    try {
      fs.writeFileSync(filePath, JSON.stringify(data));
      console.log("Dữ liệu đã được lưu trữ thành công!");
    } catch (error) {
      console.error("Lỗi khi lưu trữ dữ liệu:", error);
    }
  };

  // Sử dụng
  const users = getData();

  if (users) {
    console.log("Danh sách người dùng:", users);

    // Thêm một người dùng mới
    const newUser = { id: 2, name: "Jane Doe", age: 30 };
    users.push(newUser);

    // Lưu trữ dữ liệu sau khi thay đổi
    saveData(users);
  }

  return (
    <Fragment>
      <header className={classes.header}>
        <h1>ReactMeals</h1>
        <HeaderCartButton onShowCart={props.onShowCart} />
      </header>
      <div className={classes["main-image"]}>
        <img src={mealsImage} alt="a table full of delicious food!"></img>
      </div>
    </Fragment>
  );
};

export default Header;
